import React from "react";
const style = {
    fontFamily: "Trattatello",
}

const tableStyle = {
    color: "black",
    border: "1px solid black",
    padding: "8px",
    fontFamily: "Trattatello",
    fontSize: "20px"
  };



  const tables = {
    display: "inline-table",
    float: "left",
  }

export const MultiDayWeatherForecast = ({forecast, days}) => {
    return (
        <div>
            <h2 style = {style}>{days} day forecast</h2>
            <table style = {tableStyle}>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Weather</th>
                        <th>Temperature</th>
                        <th>Humidity</th>
                        <th>Wind Speed (MPH)</th>
                    </tr>
                </thead>
                <tbody>
                    {forecast.forecastday.map(day => (
                        <tr key={day.date}>
                            <td>{day.date}</td>
                            <td> <img src = {day.day.condition.icon}/></td>
                            <td>{day.day.maxtemp_f} F / {day.day.mintemp_f} F</td>
                            <td>{day.day.avghumidity}%</td>
                            <td>{day.day.maxwind_mph}</td>
                        </tr>
                    ))}
                </tbody>
            </table>


        </div>
    )
}
