import { useState } from "React"
import data from "./data/country_data.json"
import Select from 'react-select';
import axios from 'axios'

export default function SearchBar() {
    //  [query, setquery] = useState('')
    //  handleChange = (e) => {
    //     (e.target.value)
    // }
    // return (
    // <div>
    //     <form>
    // <       input type="search" value={query} onChange={handleChange}/>
    //     </form>
    // </div>
    // )

    const handleChange = (selectedOptions) => {
        const api_url = `http://api.weatherapi.com/v1/forecast.json?key=dc83cf84d30c434599164146232101&q=${selectedOptions.name}&days=5`
        axios.get(api_url)

        .then(res => {
            const data = res.body;
            console.log(data);
        })
    }
    return (
        <Select
          options={data}
          getOptionLabel={(option) => option.name}
          getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
          placeholder = {'Enter a city to find it\'s weather'}
          onChange={handleChange}
        />

    )
}

