    const [value, setValue] = useState({});
    return (
        <Select
          name="accounts"
          options={data}
          value={value}
          onChange={setValue}
          getOptionLabel={(option) => option.name}
          getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
        />
    )
}

