import React from "react";
import Table from 'react-bootstrap/Table';


const tableStyle = {
    color: "black",
    border: "1px solid black",
    padding: "8px",
    fontFamily: "Trattatello",
    fontSize: "20px"
  };

const hourlyTable = {
    color: "black",
    border: "1px solid black",
    padding: "8px",
    fontFamily: "Trattatello",
    fontSize: "20px",
  };


export const MultiDayWeatherForecast = ({forecast}) => {
    return (
        <div class>

            <table style = {hourlyTable}>
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Weather</th>
                        <th>Temperature(Feels Like)</th>
                        <th>Humidity</th>
                        <th>Wind Speed (MPH)</th>
                    </tr>
                </thead>
                <tbody>
                    {forecast.forecastday[0].hour.filter((hour) => {
                        console.log(hour);
                        if (hour == 0 || hour == 3 || hour == 6 || hour == 9 || hour == 12 ||
                            hour == 15 || hour == 18 || hour == 21){
                                console.log(hour);
                                return hour;
                            } 
                    }). map(hour => (
                        <tr key={hour.time}>
                            <td>{hour.time}</td>
                            <td> <img src = {hour.condition.icon}/></td>
                            <td>{hour.temp_f}({hour.feelslike_f}) F</td>
                            <td>{hour.humidity}%</td>
                            <td>{hour.wind_mph}</td>
                        </tr>
                    ))}
                </tbody>
            </table>


            <table style = {tableStyle}>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Weather</th>
                        <th>Temperature</th>
                        <th>Humidity</th>
                        <th>Max Wind Speed (MPH)</th>
                    </tr>
                </thead>
                <tbody>
                    {forecast.forecastday.map(day => (
                        <tr key={day.date}>
                            <td>{day.date}</td>
                            <td> <img src = {day.day.condition.icon}/></td>
                            <td>{day.day.maxtemp_f} F / {day.day.mintemp_f} F</td>
                            <td>{day.day.avghumidity}%</td>
                            <td>{day.day.maxwind_mph}</td>
                        </tr>
                    ))}
                </tbody>
            </table>


        </div>
    )

}
