import React from "react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import { useState } from 'react';
import { Grid } from "@mui/material";

const gridStyle = {
    overflowX: "auto", overflowY: "auto"
  }

const style = {
    color: "#c4a35a",
    fontFamily: "Arial",
};
  const tableStyle = {
    padding: "1px",
    borderCollapse:  "collapse",
    borderSpacing: "0",
    width: "auto%",
    float:"left"
  };

  const hourlyTable = {
    fontSize: "12px",
    borderCollapse:  "collapse",
    borderSpacing: "0",
    width: "auto%",
    float:"right",
    padding: "0",
    textAlign:"center"

  };

  const row = {
    marginLeft:"-5px",
    marginRight:"-5px",
  }




export const Forecast = ({forecast}) => {

    let [fahrenheit, setFahrenheit] = useState(true);
    const handleChangeFahrenheit = (event) => {
        fahrenheit = !fahrenheit
        setFahrenheit(event.target.checked);
      };

    let arr = forecast.forecast.forecastday[0].hour
    arr = [arr[0], arr[3], arr[6], arr[9], arr[12], arr[15], arr[18], arr[21]]
    return (
        <div style={style}>
            <div className = {row}>
                    <h2>Today's weather in {forecast.location.name}, {forecast.location.region}</h2>
                <Grid container spacing={2} style = {gridStyle}>
                    <Grid item xs={6} md={4}>
                    <TableContainer component = {Paper}>
                    <Table sx={{ maxWidth: 300 }} aria-label="simple table" style = {tableStyle}>
                        <TableRow><img src = {forecast.current.condition.icon}/><p>{forecast.current.condition.text}</p></TableRow>
                        <TableRow>Sunrise: {forecast.forecast.forecastday[0].astro.sunrise}</TableRow>
                        <TableRow>Sunset: {forecast.forecast.forecastday[0].astro.sunset}</TableRow>
                        <TableRow>Moonrise: {forecast.forecast.forecastday[0].astro.moonrise}</TableRow>
                        <TableRow>Moonset: {forecast.forecast.forecastday[0].astro.moonset}</TableRow>
                    </Table>
                    </TableContainer>
                    </Grid>

                <Grid item xs={6} md={8}>

                <TableContainer component = {Paper}>


                <Table sx={{ maxWidth: 200 }} aria-label="simple table" style = {hourlyTable}>

                    <TableHead>
                    <TableRow>
                            <TableCell>Time</TableCell>
                            <TableCell>Weather</TableCell>
                            <TableCell>
                            <div>Temperature</div> 
                                <FormControlLabel
                                control={<Switch checked={fahrenheit} onChange={handleChangeFahrenheit} />}
                                label="F/C"
                                />

                            </TableCell>
                            <TableCell>Humidity</TableCell>
                            <TableCell>Wind Speed (MPH)</TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {arr.map(hour => (
                            <TableRow key={hour.time}>
                                <TableCell>{hour.time}</TableCell>
                                <TableCell> <img src = {hour.condition.icon}/></TableCell>
                                <TableCell>{fahrenheit? hour.temp_f : hour.temp_c }Â°</TableCell>
                                <TableCell>{hour.humidity}%</TableCell>
                                <TableCell>{hour.wind_mph}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>

                </TableContainer>
                </Grid>
            </Grid>
            </div>

    </div>



    )
};
