import React from "react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
const style = {
    color: "black",
    fontFamily: "Trattatello",

  };
  const tableStyle = {
    padding: "1px",
    borderCollapse:  "collapse",
    borderSpacing: "0",
    width: "50%",
    float:"left"
  };

  const hourlyTable = {
    border: "1px solid black",
    fontSize: "12px",
    borderCollapse:  "collapse",
    borderSpacing: "0",
    width: "50%",
    float:"right",
    padding: "0",

  };

  const row = {
    marginLeft:"-5px",
    marginRight:"-5px",
  }


export const Forecast = ({forecast}) => {
    let arr = forecast.forecast.forecastday[0].hour
    arr = [arr[0], arr[3], arr[6], arr[9], arr[12], arr[15], arr[18], arr[21]]
    return (
        <div style={style}>
            <div class = {row}>
                    <h2>Today's weather in {forecast.location.name}, {forecast.location.region}</h2>
                    <TableContainer component = {Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table" style = {tableStyle}>
                        <TableRow><img src = {forecast.current.condition.icon}/><p>{forecast.current.condition.text}</p></TableRow>
                        <TableRow>Sunrise: {forecast.forecast.forecastday[0].astro.sunrise}</TableRow>
                        <TableRow>Sunset: {forecast.forecast.forecastday[0].astro.sunset}</TableRow>
                        <TableRow>Moonrise: {forecast.forecast.forecastday[0].astro.moonrise}</TableRow>
                        <TableRow>Moonset: {forecast.forecast.forecastday[0].astro.moonset}</TableRow>
                    </Table>
                    </TableContainer>



                <table style = {hourlyTable}>
                    <thead>
                        <tr>
                            <th>Time</th>
                            <th>Weather</th>
                            <th>Temperature(Feels Like)</th>
                            <th>Humidity</th>
                            <th>Wind Speed (MPH)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {arr.map(hour => (
                            <tr key={hour.time}>
                                <td>{hour.time}</td>
                                <td> <img src = {hour.condition.icon}/></td>
                                <td>{hour.temp_f}({hour.feelslike_f}) F</td>
                                <td>{hour.humidity}%</td>
                                <td>{hour.wind_mph}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

    </div>



    )
};
