import React from "react";

export const MultiDayWeatherForecast = ({forecast}) => {
    return (
          <table>
            <thead>
                <tr>
                    <th>Date</th>
                    {/* <th>Weather</th> */}
                    <th>Temperature</th>
                    <th>Humidity</th>
                    <th>Max Wind Speed (MPH)</th>
                </tr>
            </thead>
            <tbody>
                {forecast.forecastday.map(day => (
                    <tr key={day.date}>
                        <td>{day.date}</td>
                        {/* <td> <img src = {day.condition.icon}/></td> */}
                        <td>`${day.maxtemp_f} F / ${day.mintemp_f} F`</td>
                        <td>{day.avghumidity}</td>
                        <td>{day.maxwind_mph}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    )

}
