import logo from './logo.svg';
import './App.css';
// import data from "./data/country_data.json"
import data from "/weather_forecast_app-main/weather_api_service/weather_api_service/data"
import Select from 'react-select';
import { Forecast } from './components/forecast';
import { useState } from 'react';
import { MultiDayWeatherForecast } from './components/weatherTable';

function App() {
  const [select, setSelect] = useState(false);
  const [weatherData, setWeatherData] = useState({})

  const handleChange =  async (selectedOptions) => {
    await fetch(`http://127.0.0.1:5900/forecast/${selectedOptions.name}/5`, {
        method: "get"
    }).then(res => res.json())
    .then((data) => {
      console.log(data)
      setSelect(true)
      setWeatherData(data)
    });
  }

  return (
    <div className="App">
      <header className="App-header">
      <Select
        options={data}
        getOptionLabel={(option) => option.name}
        getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
        placeholder = {'Enter a city to find it\'s weather'}
        onChange={handleChange}
      />

      {select && <Forecast forecast={weatherData}/>}
      {select && <MultiDayWeatherForecast forecast = {weatherData.forecast} />}
      </header>

    </div>
  );
}

export default App;
