import React from "react";
const style = {
    color: "black",
    fontFamily: "Trattatello"
  };
  const tableStyle = {
    color: "black",
    border: "1px solid black",
    padding: "1px",
    fontFamily: "Trattatello"
  };
export const Forecast = ({forecast}) => {
    return (
        <div style={style}>
            <h1>Weather in {forecast.location.name}, {forecast.location.region}</h1>
            <img src = {forecast.current.condition.icon}/>
            <table style = {tableStyle}>
            <tr>Sunrise: {forecast.forecastday[0].astro.sunrise}</tr>
            <tr>Sunset: {forecast.forecastday[0].astro.sunset}</tr>
            <tr>Moonrise: {forecast.forecastday[0].astro.moonrise}</tr>
            <tr>Moonset: {forecast.forecastday[0].astro.moonset}</tr>
            </table>
        </div>
    )
};
