import React from "react";
const style = {
    color: "black",
    fontFamily: "Trattatello"
  };
  const tableStyle = {
    color: "black",
    border: "1px solid black",
    padding: "1px",
    fontFamily: "Trattatello"
  };
  const imgClass = {
    width: "50px",
    height: "auto"

  };
export const Forecast = ({forecast}) => {
    return (
        <div style={style}>
             <h1>Today's weather in {forecast.location.name}, {forecast.location.region}</h1>
            <table>
                <tr>
                    <td> <img class = {imgClass} src = {forecast.current.condition.icon} /></td>
                    <td> <p>{forecast.current.condition.text}</p> </td>
                </tr>
            </table>
            <div>
                <table style = {tableStyle}>
                    <tr>Sunrise: {forecast.forecast.forecastday[0].astro.sunrise}</tr>
                    <tr>Sunset: {forecast.forecast.forecastday[0].astro.sunset}</tr>
                    <tr>Moonrise: {forecast.forecast.forecastday[0].astro.moonrise}</tr>
                    <tr>Moonset: {forecast.forecast.forecastday[0].astro.moonset}</tr>
                </table>
            </div>
        </div>
    )
};
