import React from "react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import { useState } from 'react';
import { Box } from "@mui/system";


export const Forecast = ({forecast}) => {

    //Hook to see if the button has been toggled or not rendering either fahrenheit or celsius
    let [fahrenheit, setFahrenheit] = useState(true); 
    const handleChangeFahrenheit = (event) => {
        fahrenheit = !fahrenheit
        setFahrenheit(event.target.checked);
      };

    let arr = forecast.forecast.forecastday[0].hour
    arr = [arr[0], arr[3], arr[6], arr[9], arr[12], arr[15], arr[18], arr[21]] // these are the hours that we want per the example
    return (
        <div>
            <h2>Today's weather in {forecast.location.name}, {forecast.location.region}</h2>

            <Box>
                <TableContainer component = {Paper}>         
                    <Table sx={{ maxWidth: 300 }} aria-label="simple table">

                        <TableHead>
                            <TableRow>
                                <TableCell>Time</TableCell>
                                <TableCell>Weather</TableCell>
                                <TableCell>
                                <div>Temperature</div> 
                                    <FormControlLabel
                                    control={<Switch checked={fahrenheit} onChange={handleChangeFahrenheit} />}
                                    label="F/C"
                                    />

                                </TableCell>
                                <TableCell>Humidity</TableCell>
                                <TableCell>Wind Speed (MPH)</TableCell>
                            </TableRow>
                        </TableHead>

                        <TableBody>
                            {arr.map(hour => (
                                <TableRow key={hour.time}>
                                    <TableCell>{hour.time}</TableCell>
                                    <TableCell> <img src = {hour.condition.icon}/></TableCell>
                                    <TableCell>{fahrenheit? hour.temp_f : hour.temp_c }Â°</TableCell>
                                    <TableCell>{hour.humidity}%</TableCell>
                                    <TableCell>{hour.wind_mph}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>

                </TableContainer>
            </Box>



    </div>



    )
};
