import './App.css';
import data from "./data/country_data.json"
import Select from 'react-select';
import { Forecast } from './components/forecast';
import { useState } from 'react';
import { MultiDayWeatherForecast } from './components/weatherTable';
import AsyncSelect from 'react-select/async';

function App() {
  const [select, setSelect] = useState(false);
  const [weatherData, setWeatherData] = useState({})
  const [numberDays, setNumberDays] = useState(5)
  const [city, setCity] = useState("")


  const handleChange =   (selectedOptions, numberDays) => {
    console.log(numberDays, "here")
    setCity(selectedOptions)
     fetch(`http://127.0.0.1:5900/forecast/${selectedOptions.name}/${numberDays}`, {
        method: "get"
    }).then(res => res.json())
    .then((data) => {
      setSelect(true)
      setWeatherData(data)
      setSelectedValue(selectedOptions.name);
    });
  }


  const editBoth =  (options) => {
    setNumberDays(options.value)
    if(city == ""){ // checks to see if the city hasn't been set yet
      return
    }
     handleChange(city, options.value)

  }
  const newData = async () => {
    return fetch(`http://127.0.0.1:5900/countries/${city}`).then(res => res.json())
  }

  const days = [
    {label: "3", value: 3},
    {label: "5", value: 5},
    {label: "7", value: 7},
  ]
  const [selectedValue, setSelectedValue] = useState(null);



  return (
    <div className="App">
      <header className="App-header">
        <span>Welcome to Akshay Shivkumar's weather app!
        <div className="select">
          <AsyncSelect
            cacheOptions
            defaultOptions
            value={selectedValue}
            getOptionLabel={(option) => option.name}
            getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
            placeholder = {'Enter a city to find it\'s weather'}
            onChange={(city) => handleChange(city, numberDays)}
            // onInputChange = {handleInputChange}
            loadOptions = {newData}
          />

          <Select
            options={days}
            placeholder = {'Number of days of forecast'}
            onChange={editBoth}
          />

      </div>
      </span>
      {select && <Forecast forecast={weatherData}/>}
      {select && <MultiDayWeatherForecast forecast = {weatherData.forecast} days = {numberDays} />}
      </header>

    </div>
  );
}

export default App;
