import { TableContainer } from "@mui/material";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from "react";
const style = {
    fontFamily: "Trattatello",
}

const tableStyle = {
    color: "black",
    border: "1px solid black",
    padding: "8px",
    fontFamily: "Trattatello",
    fontSize: "20px"
  };



  const tables = {
    display: "inline-table",
    float: "left",
  }

export const MultiDayWeatherForecast = ({forecast, days}) => {
    return (
        <div>
            <h2 style = {style}>{days} day forecast</h2>
            <TableContainer>
            <Table sx={{ maxWidth: 300 }} aria-label="simple table" style = {tableStyle}>
                <TableHead>
                    <TableRow>
                        <TableCell>Date</TableCell>
                        <TableCell>Weather</TableCell>
                        <TableCell>Temperature</TableCell>
                        <TableCell>Humidity</TableCell>
                        <TableCell>Wind Speed (MPH)</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {forecast.forecastday.map(day => (
                        <TableRow key={day.date}>
                            <TableCell>{day.date}</TableCell>
                            <TableCell> <img src = {day.day.condition.icon}/></TableCell>
                            <TableCell>{day.day.maxtemp_f} F / {day.day.mintemp_f} F</TableCell>
                            <TableCell>{day.day.avghumidity}%</TableCell>
                            <TableCell>{day.day.maxwind_mph}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
            </TableContainer>


        </div>
    )
}
