import logo from './logo.svg';
import './App.css';
import data from "./data/country_data.json"
import Select from 'react-select';
import { Forecast } from './components/forecast';
import { useState } from 'react';
import { MultiDayWeatherForecast } from './components/weatherTable';

function App() {
  const [select, setSelect] = useState(false);
  const [weatherData, setWeatherData] = useState({})
  const [numberDays, setNumberDays] = useState(5)
  const [city, setCity] = useState("")


  const handleChange =  async (selectedOptions) => {
    setCity(selectedOptions)
    await fetch(`http://127.0.0.1:5900/forecast/${selectedOptions.name}/${numberDays}`, {
        method: "get"
    }).then(res => res.json())
    .then((data) => {
      console.log(data)
      setSelect(true)
      setWeatherData(data)
    });
  }

  const numDays = (options) => {
    setNumberDays(options)
  }

  const editBoth = async () => {
    if(!city){
      await handleChange(city)
    }
    numDays(numberDays)

  }
  const newData = async () => {
    await fetch(`http://127.0.0.1:5900/countries/${city}`, {
      method: "get"
    }).then(res => res.json)
    .then(data => console.log(data))
  }

  const days = [
    {label: "3", value: 3},
    {label: "5", value: 5},
    {label: "7", value: 7},
  ]

  return (
    <div className="App">
      <header className="App-header">
      <Select
        options={data}
        getOptionLabel={(option) => option.name}
        getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
        placeholder = {'Enter a city to find it\'s weather'}
        onChange={handleChange}
      />

      <Select
        options={days}
        placeholder = {'Number of days'}
        onChange={editBoth}
      />

      {select && <Forecast forecast={weatherData}/>}
      {select && <MultiDayWeatherForecast forecast = {weatherData.forecast} />}
      </header>

    </div>
  );
}

export default App;
