import logo from './logo.svg';
import './App.css';
import data from "./data/country_data.json"
import Select from 'react-select';

function App() {
  let responseData = null;
  const handleChange = (selectedOptions) => {
    fetch(`http://127.0.0.1:5900/forecast/${selectedOptions.name}/5`, {
        method: "get"
    }).then(res => res.json())
    .then((data) => {
      console.log(data);
      responseData = data;
    });
}

  return (
    <div className="App">
      <header className="App-header">
      <Select
        options={data}
        getOptionLabel={(option) => option.name}
        getOptionValue={(option) => option.geonameid} // It should be unique value in the options. E.g. ID
        placeholder = {'Enter a city to find it\'s weather'}
        onChange={handleChange}
      />
      <Forecast />
      </header>

    </div>
  );
}

export default App;
