import { TableContainer } from "@mui/material";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import React from "react";
import { Box } from "@mui/system";
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import { useState } from 'react';

export const MultiDayWeatherForecast = ({forecast, days}) => {
    let [fahrenheit, setFahrenheit] = useState(true);
    const handleChangeFahrenheit = (event) => {
        fahrenheit = !fahrenheit
        setFahrenheit(event.target.checked);
      };
    return (
        <div>
            <h2>{days} day forecast</h2>
            <Box>
            <TableContainer component = {Paper}>
            <Table sx={{ maxWidth: 300 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Date</TableCell>
                        <TableCell>Weather</TableCell>
                        <TableCell>
                            <div>Temperature</div> 
                                <FormControlLabel
                                control={<Switch checked={fahrenheit} onChange={handleChangeFahrenheit} />}
                                label="F/C"
                                />

                            </TableCell>
                        <TableCell>Humidity</TableCell>
                        <TableCell>Wind Speed (MPH)</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {forecast.forecastday.map(day => (
                        <TableRow key={day.date}>
                            <TableCell>{day.date}</TableCell>
                            <TableCell> <img src = {day.day.condition.icon}/></TableCell>
                            <TableCell>{ fahrenheit ? `${day.day.maxtemp_f}/${day.day.mintemp_f}°`: `${day.day.maxtemp_c}/${day.day.mintemp_c}`}°</TableCell>
                            <TableCell>{day.day.avghumidity}%</TableCell>
                            <TableCell>{day.day.maxwind_mph}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>

            </Table>

            </TableContainer>
            </Box>


        </div>
    )
}
